fun maximalNetworkRank(n: Int, roads: Array<IntArray>): Int {
    val matrix = Array(n) { IntArray(n) }
    val sum = IntArray(n)
    for (road in roads) {
        val i = road[0]
        val j = road[1]
        matrix[i][j] = 1
        matrix[j][i] = 1
        sum[i] += 1
        sum[j] += 1
    }
    var max = 0
    for (i in sum.indices) {
        for (j in i + 1 until sum.size) {
            max = maxOf(max, sum[i] + sum[j] - matrix[i][j])
        }
    }
    return max
}